import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { user, logout } = useAuth();

  return (
    <nav className="bg-coffee-cream border-b border-amber-200">
      <div className="max-w-7xl mx-auto px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Logo Brand */}
          <Link to="/" className="flex items-center space-x-3 group">
            <img 
              src="/images/icon_logo.jpg" 
              alt="Kape Ko"
              className="h-10 w-auto rounded-lg shadow-sm group-hover:shadow-md transition-shadow duration-300"
            />
            <span className="text-xl font-bold text-coffee-dark hidden sm:block group-hover:text-coffee-brown transition-colors duration-300 font-heading">
              Kape Ko
            </span>
          </Link>

          {/* Navigation - Desktop */}
          <div className="hidden md:flex items-center space-x-8">
            <Link 
              to="/" 
              className="text-coffee-dark hover:text-coffee-brown transition-colors font-medium font-body"
            >
              Home
            </Link>
            <span className="text-amber-300">|</span>
            <Link 
              to="/menu" 
              className="text-coffee-dark hover:text-coffee-brown transition-colors font-medium font-body"
            >
              Menu
            </Link>
          </div>

          {/* Auth Section */}
          <div className="hidden md:flex items-center space-x-4">
            {user ? (
              <div className="flex items-center space-x-4">
                <span className="text-coffee-dark font-medium font-body">Hello, {user.name}</span>
                <button 
                  onClick={logout}
                  className="bg-coffee-brown text-white px-6 py-2 rounded-full font-medium hover:bg-coffee-dark transition-colors font-body"
                >
                  Sign Out
                </button>
              </div>
            ) : (
              <button className="bg-coffee-brown text-white px-6 py-2 rounded-full font-medium hover:bg-coffee-dark transition-colors font-body">
                Sign In
              </button>
            )}
          </div>

          {/* Mobile Menu Button */}
          <button 
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden flex flex-col space-y-1 p-2"
          >
            <span className={`w-6 h-0.5 bg-coffee-dark transition-all duration-300 ${isMenuOpen ? 'rotate-45 translate-y-1.5' : ''}`}></span>
            <span className={`w-6 h-0.5 bg-coffee-dark transition-all duration-300 ${isMenuOpen ? 'opacity-0' : ''}`}></span>
            <span className={`w-6 h-0.5 bg-coffee-dark transition-all duration-300 ${isMenuOpen ? '-rotate-45 -translate-y-1.5' : ''}`}></span>
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden mt-4 pb-4 border-t border-amber-200">
            <div className="flex flex-col space-y-4 pt-4">
              <Link 
                to="/" 
                className="text-coffee-dark hover:text-coffee-brown transition-colors font-medium font-body"
                onClick={() => setIsMenuOpen(false)}
              >
                Home
              </Link>
              <Link 
                to="/menu" 
                className="text-coffee-dark hover:text-coffee-brown transition-colors font-medium font-body"
                onClick={() => setIsMenuOpen(false)}
              >
                Menu
              </Link>
              {user ? (
                <div className="flex flex-col space-y-2">
                  <span className="text-coffee-dark font-medium font-body">Hello, {user.name}</span>
                  <button 
                    onClick={() => {
                      logout();
                      setIsMenuOpen(false);
                    }}
                    className="bg-coffee-brown text-white px-6 py-2 rounded-full font-medium hover:bg-coffee-dark transition-colors font-body w-fit"
                  >
                    Sign Out
                  </button>
                </div>
              ) : (
                <button 
                  className="bg-coffee-brown text-white px-6 py-2 rounded-full font-medium hover:bg-coffee-dark transition-colors font-body w-fit"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Sign In
                </button>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
