import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const ModernNavbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { user, logout } = useAuth();

  return (
    <nav className="fixed top-0 left-0 right-0 bg-pure-white/80 backdrop-blur-md border-b border-gray-100 z-50">
      <div className="max-w-7xl mx-auto px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Brand */}
          <Link to="/" className="text-2xl font-bold text-modern-black font-inter hover:text-coffee-brown transition-colors">
            Kape Ko
          </Link>

          {/* Navigation - Desktop */}
          <div className="hidden md:flex items-center space-x-8">
            <Link 
              to="/" 
              className="text-modern-black hover:text-coffee-brown transition-colors font-medium font-inter"
            >
              Home
            </Link>
            <span className="text-gray-300">|</span>
            <Link 
              to="/menu" 
              className="text-modern-black hover:text-coffee-brown transition-colors font-medium font-inter"
            >
              Menu
            </Link>
          </div>

          {/* Auth Section */}
          <div className="hidden md:flex items-center space-x-4">
            {user ? (
              <div className="flex items-center space-x-4">
                <span className="text-modern-black font-medium">Hello, {user.name}</span>
                <button 
                  onClick={logout}
                  className="bg-modern-black text-pure-white px-6 py-2 rounded-full font-medium hover:bg-coffee-brown transition-colors font-inter"
                >
                  Sign Out
                </button>
              </div>
            ) : (
              <button className="bg-modern-black text-pure-white px-6 py-2 rounded-full font-medium hover:bg-coffee-brown transition-colors font-inter">
                Sign In
              </button>
            )}
          </div>

          {/* Mobile Menu Button */}
          <button 
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden flex flex-col space-y-1 p-2"
          >
            <span className={`w-6 h-0.5 bg-modern-black transition-all duration-300 ${isMenuOpen ? 'rotate-45 translate-y-1.5' : ''}`}></span>
            <span className={`w-6 h-0.5 bg-modern-black transition-all duration-300 ${isMenuOpen ? 'opacity-0' : ''}`}></span>
            <span className={`w-6 h-0.5 bg-modern-black transition-all duration-300 ${isMenuOpen ? '-rotate-45 -translate-y-1.5' : ''}`}></span>
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden mt-4 pb-4 border-t border-gray-100">
            <div className="flex flex-col space-y-4 pt-4">
              <Link 
                to="/" 
                className="text-modern-black hover:text-coffee-brown transition-colors font-medium font-inter"
                onClick={() => setIsMenuOpen(false)}
              >
                Home
              </Link>
              <Link 
                to="/menu" 
                className="text-modern-black hover:text-coffee-brown transition-colors font-medium font-inter"
                onClick={() => setIsMenuOpen(false)}
              >
                Menu
              </Link>
              {user ? (
                <div className="flex flex-col space-y-2">
                  <span className="text-modern-black font-medium">Hello, {user.name}</span>
                  <button 
                    onClick={() => {
                      logout();
                      setIsMenuOpen(false);
                    }}
                    className="bg-modern-black text-pure-white px-6 py-2 rounded-full font-medium hover:bg-coffee-brown transition-colors font-inter w-fit"
                  >
                    Sign Out
                  </button>
                </div>
              ) : (
                <button 
                  className="bg-modern-black text-pure-white px-6 py-2 rounded-full font-medium hover:bg-coffee-brown transition-colors font-inter w-fit"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Sign In
                </button>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default ModernNavbar;
